article_index.map:
function (doc) {
     for(var n = 0; n < doc.indices.length; n++) {
         emit(doc.indices[n].indices_KEY, parseInt(doc.indices[n].indices, 10)); 
     }
}

article_index.reduce:
function (key, values, rereduce) {
    var max = -1;
    values.forEach(function(value) {
        if (max < value) {
            max = value;
        }
    });
    return max;
}
